ArrayUtil=function(){function e(){}e.equals=function(e,t){var n=e.length;if(n!=t.length)return false;while(n--)if(e[n]!=t[n])return false;return true};e.addItemsAt=function(e,t,n){if(t.length==0)return false;var r=t.concat();r.splice(0,0,n,0);e.splice.apply(null,r);return true};e.removeDuplicates=function(t){return t.filter(e._removeDuplicatesFilter)};e._removeDuplicatesFilter=function(e,t,n){return t==0?true:n.lastIndexOf(e,t-1)==-1};e.removeItem=function(e,t){var n=e.indexOf(t);var r=0;while(n!=-1){e.splice(n,1);n=e.indexOf(t,n);r++}return r};e.removeItems=function(e,t){var n=false;var r=e.length;while(r--){if(t.indexOf(e[r])!=-1){e.splice(r,1);n=true}}return n};e.retainItems=function(e,t){var n=false;var r=e.length;while(r--){if(t.indexOf(e[r])==-1){e.splice(r,1);n=true}}return n};e.contains=function(e,t){var n=e.indexOf(t,0);var r=0;while(n!=-1){n=e.indexOf(t,n+1);r++}return r};e.containsAny=function(e,t){var n=t.length;while(n--)if(e.indexOf(t[n])!=-1)return true;return false};e.getIndexOfDifference=function(e,t,n){var r=n-1;while(++r<e.length)if(e[r]!=t[r])return r;return-1};e.randomize=function(e){for(var t=e.length-1;t>0;t--){var n=Math.floor(Math.random()*(t+1));var r=e[t];e[t]=e[n];e[n]=r}return e};e.getLowestValue=function(e){return Math.min.apply(Math,e)};e.getHighestValue=function(e){return Math.max.apply(Math,e)};return e}()
