var ValidationUtil=function(){function e(){}e.contains=function(e,t){return StringUtil.contains(e,t)>0};e.isEmail=function(t){if(t.length<6||e.isEmpty(t)){return false}if(StringUtil.contains(t," ")>0){return false}if(StringUtil.contains(t,"@")!=1){return false}var n=t.indexOf("@");var r=t.lastIndexOf(".");if(r<n+2||r>t.length-3){return false}return true};e.isEmpty=function(e){if(StringUtil.removeWhitespace(e)==""){return true}else{return false}};e.isPhone=function(e){return StringUtil.getNumbersFromString(e).length>=10};e.isZip=function(e){var t=StringUtil.getNumbersFromString(e).length;return t==5||t==9};return e}()
